export const IconCheck = ({
  size,
  inTag,
}: {
  size?: number
  inTag?: boolean
}) =>
  !inTag ? (
    <img
      src="/img/check.png"
      width={size ?? 24}
      height={size ?? 24}
      style={{
        position: 'absolute',
        right: 20,
      }}
      alt=""
    />
  ) : (
    <img src="/img/check.png" width={size ?? 24} height={size ?? 24} alt="" />
  )

export const IconCancel = ({ size }: { size?: number }) => (
  <img
    src="/img/close.png"
    width={size ?? 24}
    height={size ?? 24}
    alt=""
    style={{ height: 'fit-content' }}
  />
)
